const chaiHttp = require('chai-http');
const chai = require('chai');
const assert = chai.assert;
const server = require('../server.js');

chai.use(chaiHttp);

suite('Functional Tests', () => {
    /// Test #1
    test('#singleStockGETRequest',function(done){
        console.log("Ran test 1")
        chai.request(server).get('/api/stock-prices?stock=GOOG').end(
            (err,res)=>{
                assert.deepEqual(res.body,{"stockData":{"stock":"GOOG","price":786.90,"likes":0}},'Looked up single stock')
            }
        );
        done();
    })

    // Test #2
    test('#singleStocksingleLikeGETRequest',function(done){
        chai.request(server).get('/api/stock-prices?stock=GOOG&like=true').end(
            (err,res)=>{
                assert.deepEqual(res.body,{"stockData":{"stock":"GOOG","price":786.90,"likes":1}},'Looked up single stock w// single like')
            }
        );
        done();
    })

    // Test #3
    test('#doubleLikingStock',function(done){
        chai.request(server).get('/api/stock-prices?stock=GOOG&like=true').end(
            (err,res)=>{
                assert.deepEqual(res.body,{"stockData":{"stock":"GOOG","price":786.90,"likes":1}},'Re-liked same stock')
            }
        );
        done();
    })

    // Test #4
    test('#twoStockGETRequest',function(done){
        chai.request(server).get('/api/stock-prices?stock=GOOG&stock=MSFT').end(
            (err,res)=>{
                let result={"stockData":[{"stock":"MSFT","price":62.30,"rel_likes":0},{"stock":"GOOG","price":786.90,"rel_likes":0}]}
                assert.deepEqual(res.body,result,'Looked up two stocks')
            }
        );
        done();
    })

    // Test #5
    test('#doubleStockLikeGETRequest',function(done){
        chai.request(server).get('/api/stock-prices?stock=GOOG&stock=MSFT&like=true').end(
            (err,res)=>{
                let result={"stockData":[{"stock":"MSFT","price":62.30,"rel_likes":-1},{"stock":"GOOG","price":786.90,"rel_likes":1}]}
                assert.deepEqual(res.body,result,'Looked up two stocks and liked them')
            }
        );
        done();
    })  

});

